
public class SumOfArrrayValues {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] array = {100, 200, 300, 400, 500, 600}; 
		int sum = 0; 
		for( int num : array) 
		{ 
		 sum = sum+num; 
		} 
		
		 System.out.println("Sum of array elements is:"+sum);
		 
	}

}
